import styled from "styled-components";
import React from "react";

const StyledButton = styled.button`
  cursor: pointer;
  padding: 0.5em, 1.5em;
  font-family: sans-serif;
  font-size: 1.25em;
  border: 1px solid ${(props) => (props.outline ? "lightblue" : "white")};
  background-color: ${(props) => (props.outline ? "white" : "lightblue")};
  color: ${(props) => (props.outline ? "lightblue" : "white")};
  width: 100px ${(props) => props.fullWidth};
  &:hover {
    border-color: cornflowerblue 1px solid ${(props) =>
      props.outline ? "lightblue" : "cornflowerblue"};
    background-color: cornflowerblue ${(props) =>
      props.outline ? "lightblue" : "cornflowerblue"}
    color: white;
  }

`;

export default function Button() {
  return (
    <div>
      <StyledButton></StyledButton>
    </div>
  );
}
