import styled from "styled-components";

export default function Card() {
  return (
    <>
      const CardContainer = styled.div` border: 1px solid; border-color:
      rgba(192, 192, 192, 0.5); border-radius: 0.5em; width: 100% $
      {(props) => props.width}
      -webkit-box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.25); -moz-box-shadow:
      0px 5px 5px 0px rgba(0, 0, 0, 0.25); box-shadow: 0px 5px 5px 0px rgba(0,
      0, 0, 0.25); `; const CardImage = styled.img` width: 100%
      border-top-left-radius: 0.5em; border-top-right-radius: 0.5em; `; const
      CardBody = styled.div` padding: 1em; `; const CardPrice = styled.h5`
      font-family: sans-serif; font-size: 1.25em; font-weight: 600; margin: 0;
      margin-bottom: 0.5em; `; const CardProductName = styled.h6` font-family:
      sans-serif font-size: 1.25em font-weight: 600 margin: 0; margin-bottom:
      0.5em; color: gray; `;
    </>
  );
}
