import styled from "styled-components";

export const CardContainer = styled.div`
  border: 1px solid;
  border-color: rgba(192, 192, 192, 0.5);
  border-radius: 0.5em;
  width: 100% ${(props) => props.width}
  -webkit-box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.25);
  -moz-box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.25);
  box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.25);
`;

export const CardImage = styled.img`
  width: 100%
  border-top-left-radius: 0.5em;
  border-top-right-radius: 0.5em;
`;

export const CardBody = styled.div`
  padding: 1em;
`;

export const CardPrice = styled.h5`
  font-family: sans-serif;
  font-size: 1.25em;
  font-weight: 600;
  margin: 0;
  margin-bottom: 0.5em;
`;

export const CardProductName = styled.h6`
    font-family: sans-serif
    font-size: 1.25em
    font-weight: 600
    margin: 0;
    margin-bottom: 0.5em;
    color: gray;
`;

export default function Card() {
  return (
    <CardContainer width="500px">
      <CardImage src="https://images.unsplash.com/photo-1521369909029-2afed882baee?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1470&q=80" />
      <CardBody>
        <CardPrice>Rp 99000</CardPrice>
        <CardProductName>Topi Baseball</CardProductName>
        <Button />
      </CardBody>
    </CardContainer>
  );
}
