import styled from "styled-components";

export const StyledButton = styled.button`
  cursor: pointer;
  padding: 0.5em, 1.5em;
  font-family: sans-serif;
  font-size: 1.25em;
  border: ${(props) => (props.outline ? "lightblue" : "white")} 1px solid;
  background-color: ${(props) => (props.outline ? "white" : "lightblue")};
  color: ${(props) => (props.outline ? "lightblue" : "white")};
  width: auto ${(props) => (props.fullWidth !== null ? "100%" : "auto")};
  &:hover {
    border: ${(props) =>
      props.outline ? "lightblue" : "cornflowerblue"} cornflowerblue 1px solid ;
    background-color: cornflowerblue ${(props) =>
      props.outline ? "lightblue" : "cornflowerblue"}
    color: white;
  }

`;
